#header-play {
  position: fixed;
  top: 0px;
  display: flex;
  width: 100%;
  height: 60px;
  background-color: #f1f1f1;
  justify-content: space-between;
  align-items: center;
  padding: 0 20px;
  z-index: 999;
}

#header-logo {
  height: 30px;
}

#header-search input {
  width: 900px;
